INSERT INTO `v9_position_data` VALUES('60','16','23','content','1','1','array (\n  \'title\' => \'美人尖种植美女更是锦上添花\',\n  \'description\' => \'早在我国古代美女额头正中的头发往往都会往下再长一点，形成“M”型发际，即俗称“美人尖”。精致妥帖的美人尖可以让一个平凡的容貌衬托出优雅高贵的气质。长得漂亮的女性有了美人尖就更是锦上添花。目前通过美人尖种植可以让您轻而易举的拥有漂亮的美人尖，而且效果和天生的美人尖几乎没有任何区别，真的有这么神奇吗? 到底是不是真如医生所说的那么神奇，我们可以通过美人尖种植的原理中找答案。\',\n  \'thumb\' => \'http://bjshenniao.zhifa.cc/uploadfile/2015/0109/20150109015822172.jpg\',\n  \'inputtime\' => \'1420793944\',\n  \'style\' => \'\',\n)','1','60','0','','0');
